    filteredList.forEach((list, index) => {vid(list, index)});
    console.log(filteredList);
}

export const accounts = () => {
    let accounts = document.getElementsByClassName('account');

    Array.from(accounts).forEach(element => {
        let displayStyle = window.getComputedStyle(element).getPropertyValue('display');

        if (displayStyle === 'none') {
            element.style.display = 'block';
        } else {
            element.style.display = 'none';
        }
    });
};

export const addVideo = (src, name) => {

    let today = new Date();
    let dd = String(today.getDate()).padStart(2, '0');
    let mm = String(today.getMonth() + 1).padStart(2, '0'); 
    let yyyy = today.getFullYear();
    let hour = String(today.getHours()).padStart(2, '0');
    let minute = String(today.getMinutes()).padStart(2, '0');
    let second = String(today.getSeconds()).padStart(2, '0');
    today = mm + '/' + dd + '/' + yyyy + ' ' + hour + ':' + minute + ':' + second;


    let newVideo = {
        avtUser: 'penguin.png', 
        src: src,
        name: name,
        author: 'phuoctt', 
        watched: '0', 
        date: today 
    };

    playList.unshift(newVideo);
    postVideo(newVideo);
    reset();
};

export const addVideoBtn = () => {
    document.getElementById('overlay').style.display = 'block';
    document.getElementById('popupForm').style.display = 'block';
    document.getElementById('submit').addEventListener('click', function(event) {
        event.preventDefault(); // Ngăn form gửi đi khi click nút "Submit"

        document.getElementById('overlay').style.display = 'none';
        document.getElementById('popupForm').style.display = 'none';
        let src = document.getElementById('src').value;
        let name = document.getElementById('name').value;
        if (src == '' || name == '') {
            console.log('Input required');
        } else {
            addVideo(src, name);

            document.getElementById('videoForm').reset();
        }
    });

};

export const postVideo = (newVideo) => {

    const body = JSON.stringify(newVideo);

    const options = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: body
    };

    fetch('/addVideo', options)
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            console.log('Video added successfully:', data);
        })
        .catch(error => {
            console.error('Error adding video:', error);
        });
};

export function logout() {
    const username = window.username;
    var xhttp = new XMLHttpRequest();
    xhttp.open("POST", "/logout", true);
    xhttp.setRequestHeader("Content-type", "application/json"); // Change content-type

    // Gửi thông tin tài khoản và mật khẩu dưới dạng JSON
    var data = JSON.stringify({ username: username }); // Wrap username in an object
    xhttp.send(data);

    xhttp.onreadystatechange = function () {
        if (this.readyState == 4) {
            if (this.status == 200) {
                var responseData = JSON.parse(this.responseText);
                if (responseData.message == 'Success') {
                    window.location.replace("/login");
                }
            } else {
                // Handle other statuses if needed
            }
        }
    };
}
