import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { formatViews } from './function';
import { timeAgo } from './function';
import '../css/content.css'; // Đảm bảo rằng tệp content.css nằm cùng cấp với tệp Playlist.js
import '../../images/penguin.png';

const Playlist = () => {
  const [playList, setPlayList] = useState([]);

  useEffect(() => {
    const fetchPlaylist = async () => {
      try {
        const response = await axios.get('http://localhost:3001/playlist/');
        setPlayList(response.data); // Assuming response.data is an array of playlist items
      } catch (error) {
        console.error('Error fetching playlist:', error);
      }
    };

    fetchPlaylist();
  }, []);

  const renderVideo = (list, index) => (
    <div key={index} className="video-wrapper">
      <iframe
        src={list.src}
        title="YouTube video player"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
      ></iframe>
      <div className="description">
        <img src={`${list.avtUser}`} alt="Avatar" />
        <div className="des">
          <div className="name">{list.name}</div>
          <div className="author">{list.author}</div>
          <div className="late">
            <div className="watched">{formatViews(list.watched)} lượt xem</div>
            <div className="date">• {timeAgo(list.date)}</div>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="content">
      <div className="btn">
        <div class="btn">
                <button id="all" class="action-content-btn" onclick="changeBackgroundColorBtn(this)">Tất cả</button>
                <button id="musicbtn" onclick="changeBackgroundColorBtn(this)">Âm nhạc</button>
                <button id="gaming" onclick="changeBackgroundColorBtn(this)">Trò chơi</button>
                <button id="live" onclick="changeBackgroundColorBtn(this)">Trực tiếp</button>
                <button id="cartoon" onclick="changeBackgroundColorBtn(this)">Hoạt hình</button>
                <button id="game-action-adventure" onclick="changeBackgroundColorBtn(this)">Trò chơi hành động phiêu lưu</button>
                <button id="recently" onclick="changeBackgroundColorBtn(this)">Mới tải lên gần đây</button>
                <button id="new-proposal" onclick="changeBackgroundColorBtn(this)">Đề xuất mới</button>
            </div>
      </div>
      <div className="video">
        {playList.map((list, index) => renderVideo(list, index))}
      </div>
    </div>
  );
};

export default Playlist;
