import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../contexts/AuthContext"; 

import Signin from '../components/account/signin'; 
import Signup from '../components/account/signup';
import ForgetPass from '../components/account/forgetpass'; 
import HomeGuest from '../page/homeGuest';
import HomeUsers from '../page/homeUser';
import AdminUsers from '../page/adminUsers';
import AdminVideo from '../page/adminVideo';

// Component bao bọc Route để kiểm tra quyền truy cập
const PrivateRoute = ({ element, allowedRoles }) => {
  const { user } = useContext(AuthContext); 
  const userRole = user ? user.role : 'guest'; 

  if (!user || !allowedRoles.includes(userRole)) {
    return <Navigate to="/signin" />; // Chuyển hướng đến trang đăng nhập nếu không có user hoặc không có quyền truy cập
  }

  return element; // Cho phép truy cập nếu user có quyền
};

// Hàm Router
export const AppRouter = () => {
  return (
    <Router>
      <Routes>
        <Route path="/signin" element={<Signin />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/repass" element={<ForgetPass />} />
        <PrivateRoute path="/" element={<HomeGuest />} allowedRoles={['guest']} />
        <PrivateRoute path="/user" element={<HomeUsers />} allowedRoles={['user']} />
        <PrivateRoute path="/admin/user" element={<AdminUsers />} allowedRoles={['admin']} />
        <PrivateRoute path="/admin/video" element={<AdminVideo />} allowedRoles={['admin']} />
      </Routes>
    </Router>
  );
}

export default AppRouter;
