import { useEffect } from 'react';
import { Navigate, Route, useNavigate } from 'react-router-dom';

const PrivateRoute = ({ element, allowedRoles, ...rest }) => {
  const navigate = useNavigate(); // Sử dụng useNavigate thay thế cho useHistory

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (!storedUser) {
      navigate('/signin'); // Sử dụng navigate để điều hướng đến trang đăng nhập
    }
  }, [navigate]);

  const storedUser = JSON.parse(localStorage.getItem('user'));

  if (!storedUser || !allowedRoles.includes(storedUser.role)) {
    return <Navigate to="/signin" />;
  }

  return <Route {...rest} element={element} />;
};

export default PrivateRoute;
