export const formatViews = (watched) => {
    if (watched >= 1000000000) {
      return (watched / 1000000000).toFixed(1) + ' T'; // Convert to billions
    } else if (watched >= 1000000) {
      return (watched / 1000000).toFixed(1) + ' Tr'; // Convert to millions
    } else if (watched >= 1000) {
      return (watched / 1000).toFixed(1) + ' N'; // Convert to thousands
    } else {
      return watched.toString();
    }
};

export const timeAgo = (date) => {
    const currentDate = new Date();
    const inputDate = new Date(date);

    const diffMilliseconds = currentDate - inputDate;
    const diffSeconds = Math.floor(diffMilliseconds / 1000);
    const diffMinutes = Math.floor(diffSeconds / 60);
    const diffHours = Math.floor(diffMinutes / 60);
    const diffDays = Math.floor(diffHours / 24);
    const diffWeeks = Math.floor(diffDays / 7);
    const diffMonths = currentDate.getMonth() - inputDate.getMonth() + (12 * (currentDate.getFullYear() - inputDate.getFullYear()));
    const diffYears = currentDate.getFullYear() - inputDate.getFullYear();

    let day = '';

    if (diffYears > 0) {
      day += `${diffYears} năm trước`;
    }
    if (diffMonths > 0) {
      day += `${diffMonths} tháng trước`;
    } 
    if (diffWeeks > 0) {
      day += `${diffWeeks} tuần trước`;
    } 
    if (diffDays > 0) {
      day += `${diffDays} ngày trước`;
    } 
    if (diffHours > 0) {
      day += `${diffHours} giờ trước`;
    } 
    if (diffMinutes > 0) {
      day += `${diffMinutes} phút trước`;
    } 
    day += `${diffSeconds} giây trước`;
    return day;
};

