import { useEffect } from 'react';
import { Navigate, Route, Routes } from 'react-router-dom';

export const PrivateRoute = ({ element, allowedRoles, ...rest }) => {
  useEffect(() => {
    // Kiểm tra xem có thông tin user trong localStorage không
    const storedUser = localStorage.getItem('user');
    if (!storedUser) {
      // Nếu không có thông tin user, chuyển hướng đến trang đăng nhập
      // Có thể sử dụng Navigate hoặc useHistory từ React Router DOM
      // để điều hướng đến trang đăng nhập
      history.push('/signin');
    }
  }, []);

  // Lấy thông tin user từ localStorage
  const storedUser = JSON.parse(localStorage.getItem('user'));

  // Kiểm tra vai trò của user và xem có quyền truy cập route hay không
  if (!storedUser || !allowedRoles.includes(storedUser.role)) {
    return <Navigate to="/signin" />;
  }

  return <Route {...rest} element={element} />;
};

export default PrivateRoute;
