import { useEffect, useState } from 'react';
import { Navigate, Route, Routes } from 'react-router-dom';

const PrivateRoute = ({ element, ...rest }) => {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    // Kiểm tra xem token đã tồn tại và hợp lệ hay không
    const token = localStorage.getItem('token');
    if (token) {
      // Thực hiện kiểm tra token với server (nếu cần)
      // Nếu token hợp lệ, cho phép truy cập
      setAuthenticated(true);
    } else {
      // Nếu không có token, chuyển hướng đến trang đăng nhập
      setAuthenticated(false);
    }
  }, []);

  if (!authenticated) {
    return <Navigate to="/signin" />;
  }

  return <Route {...rest} element={element} />;
};

// Sử dụng PrivateRoute trong AppRouter
const AppRouter = () => {
  return (
    <Router>
      <Routes>
        <Route path="/signin" element={<Signin />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/repass" element={<ForgetPass />} />
        <Route path="/" element={<HomeGuest />} />
        <PrivateRoute path="/user" element={<HomeUsers />} />
        <PrivateRoute path="/admin/user" element={<AdminUsers />} />
        <PrivateRoute path="/admin/video" element={<AdminVideo />} />
      </Routes>
    </Router>
  );
};

export default AppRouter;
